ecc_agent_script_include.script:
var GetIPDetailsUtil = Class.create();

GetIPDetailsUtil.prototype = {
    initialize: function() {
        this.ip = probe.getParameter("ip");
		this.HttpClient = Packages.org.apache.commons.httpclient.HttpClient;
		this.GetMethod = Packages.org.apache.commons.httpclient.methods.GetMethod;
    },
    getIpDetails: function() {
		try {
			var body;
			var client = new this.HttpClient();
			ms.log('\n\nURL:' + 'http://ipwhois.app/json/' + this.ip + '\n\n');
			var get = new this.GetMethod('http://ipwhois.app/json/' + this.ip);
			var status = client.executeMethod(get);
			body = get.getResponseBodyAsString();
			ms.log("\n\n\n\nLOG: " +status + body);
			return body;
		} catch (error) {
			ms.log(error.getMessage());
			body = error.getMessage();
		} 
    },
    type: GetIPDetailsUtil
};
