sys_script_include.script:
var RestOperation = Class.create();
RestOperation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getLogTypes: function() {
		var request = new sn_ws.RESTMessageV2();
        request.setHttpMethod('get');
        request.setEndpoint(this.getParameter('sysparm_endpoint'));
        var data;
		var status;
		try {
			var response = request.execute();
			status = response.getStatusCode();
			data = response.haveError() ? response.getErrorMessage() : response.getBody();
		} catch(error) {
			status = 0;
			data = error;
		}
		data = {"status": status, "data": JSON.parse(data)};
		data = JSON.stringify(data);
// 		gs.info(data);
		return data;
	},
    type: 'RestOperation'
});
