sys_script_include.script:
var BulkOperationOnRecords = Class.create();
BulkOperationOnRecords.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	updateRecordsInBulk: function() {
		var records = new GlideRecord(this.getParameter('sysparm_table'));
		records.addQuery('sys_id','IN', this.getParameter('sysparm_ids'));
		records.query();
		records.setValue(this.getParameter('sysparm_field'), this.getParameter('sysparm_value'));
		records.updateMultiple();
		gs.info("Records are updated");
	},
    type: 'BulkOperationOnRecords'
});
