sys_script_include.script:
var SyncLogTypes = Class.create();
SyncLogTypes.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	syncLogTypes: function() {
		try {
			var tableName = this.getParameter('sysparm_table');
			var logType = new GlideRecord(tableName);
			logType.query();
			logType.deleteMultiple();
			gs.info(this.getParameter('sysparm_data'));
			var data = JSON.parse(this.getParameter('sysparm_data'));
			gs.info(typeof(data));
			data.forEach(function(entry) {
				var td = new GlideRecord(tableName);
				td.initialize();
				td.type = entry.logType;
				td.description = entry.description;
				td.insert();
			});

// 			var data = this.getParameter('sysparm_data');
// 			data.forEach(function() {

// 			});
		} catch (error) {
			gs.error(error);
		}
	},
    type: 'SyncLogTypes'
});
